import React, { Component } from "react";
import { Link } from "react-router-dom";
import "./Register.css";
import compeny from "../../Image/compeny.png";
import logo from "../../Image/logo.png";
import man from "../../Image/man.ico";
import Footer from "../../components/LandingPage/Footer/Footer";
import NavFixTop from "../NavFixTop/NavFixTop";
import axios from "axios";

class Register extends Component {
  constructor(props) {
    super(props);
    this.state = {
      register: {
        name: "",
        email: "",
        password: "",
        rePassword: ""
      },
      togleMata1: true,
      togleMata2: true
      // isLoading: ""
    };
  }

  postRegister = e => {
    const dataStatePost = this.state.register;

    this.setState = {
      isLoading: true
    };
    axios
      .post("https://vinum.serveo.net/api/users", dataStatePost)
      .then(res => {
        console.log("status ok => ", res);
        alert("Register Berhasil", res);
        this.setState({
          register: {
            name: "",
            email: "",
            password: "",
            rePassword: ""
          }
          // isLoading: false
        });
      })
      .catch(err => {
        // alert("nama atau email sudah digunakan", err);
      });
  };

  handleChangeRegister = e => {
    let changeRegister = { ...this.state.register };
    changeRegister[e.target.name] = e.target.value;
    this.setState({
      register: changeRegister
    });
  };

  handleSubmitRegister = e => {
    e.preventDefault();
    const { password, rePassword, name, email } = this.state.register;
    if (name === "" || email === "" || password === "") {
      alert("Nama, email , password tidak boleh kosong");
    } else if (password === rePassword) {
      this.postRegister();
    } else {
      alert("Password tidak sama");
    }
  };

  handleToggleMata1 = () => {
    this.setState({
      togleMata1: !this.state.togleMata1
    });
  };
  handleToggleMata2 = () => {
    this.setState({ togleMata2: !this.state.togleMata2 });
  };
  render() {
    const { name, email, password, rePassword } = this.state.register;
    // const { isLoading } = this.state;

    // if (isLoading) {
    //   return <h1>Loading bang ......</h1>;
    // }
    return (
      <div className="boody">
        <NavFixTop />
        <div className="container register">
          <div className="row">
            <div className="col all">
              <div className="row">
                <div className="col-lg-4 image">
                  <h1>Register</h1>
                  <p>Let's join in our application</p>
                  <img src={compeny} className="img-fluid" alt="" />
                </div>
                <div className="col-lg-6 innput">
                  {" "}
                  <img src={man} alt="" width="40" className="float-right" />
                  <img src={logo} alt="" className="ml-1" />
                  <p className="ml-1">Change the world with your hands</p>
                  <form autoComplete="off">
                    <div className="form-group">
                      <div className="icon">
                        <i className="fa fa-user"></i>
                        <input
                          type="text"
                          className="form-control  shadow-none"
                          style={{ fontWeight: "100", fontFamily: "Domine" }}
                          // id="exampleInputPassword1"
                          placeholder="Username"
                          name="name"
                          value={name}
                          onChange={this.handleChangeRegister}
                          required
                        />
                      </div>
                    </div>
                    <div className="form-group">
                      <div className="icon">
                        <i className="fa fa-envelope-open icon"></i>
                        <input
                          type="email"
                          className="form-control  shadow-none"
                          style={{ fontWeight: "100", fontFamily: "Domine" }}
                          // id="exampleInputPassword1"
                          placeholder="Email"
                          name="email"
                          value={email}
                          onChange={this.handleChangeRegister}
                        />
                      </div>
                    </div>
                    <div className="form-group">
                      <div className="icon">
                        <i className="fa fa-key icon"></i>
                        <input
                          type={this.state.togleMata1 ? "password" : "text"}
                          className="form-control  shadow-none"
                          style={{ fontWeight: "100", fontFamily: "Domine" }}
                          // id="exampleInputPassword1"
                          placeholder="Password"
                          name="password"
                          value={password}
                          onChange={this.handleChangeRegister}
                          pattern=".{8,}"
                        />
                        <i className="fa fa-eye" onClick={this.handleToggleMata1}>
                          {/* Show or Hide */}
                        </i>
                      </div>
                    </div>
                    <div className="form-group">
                      <div className="icon">
                        <i className="fa fa-key icon"></i>
                        <input
                          type={this.state.togleMata2 ? "password" : "text"}
                          className="form-control  shadow-none"
                          style={{ fontWeight: "100", fontFamily: "Domine" }}
                          // id="exampleInputPassword1"
                          placeholder="Re-Password"
                          name="rePassword"
                          value={rePassword}
                          onChange={this.handleChangeRegister}
                          pattern=".{8,}"
                        />
                        <i className="fa fa-eye" onClick={this.handleToggleMata2}>
                          {/* Show or Hide */}
                        </i>
                      </div>
                    </div>
                    <button
                      type="button"
                      className="btn btn-primary"
                      onClick={this.handleSubmitRegister}
                    >
                      Submit
                    </button>
                    <Link to="/login">
                      <small>Already Have an Account?</small>
                    </Link>
                  </form>
                </div>
              </div>
            </div>
          </div>
        </div>
        <Footer />
      </div>
    );
  }
}

export default Register;
